DROP TABLE IF EXISTS TRANSPORT;DROP TABLE IF EXISTS ROUTE;CREATE TABLE ROUTE(    ROUTE_ID        INT AUTO_INCREMENT PRIMARY KEY,    NUMBER_ROUTE    INT UNIQUE NOT NULL,    LENGTH          DOUBLE     NOT NULL,    LAP_TIME        INT        NOT NULL,    NUMBER_OF_STOPS INT        NOT NULL);CREATE TABLE TRANSPORT(    TRANSPORT_ID        INT AUTO_INCREMENT PRIMARY KEY,    TYPE                ENUM ('BUS','TRAM','TROLLEY') NOT NULL,    FUEL_TYPE           ENUM ('ELECTRIC','GASOLINE')  NOT NULL,    REGISTER_NUMBER     VARCHAR(100)                  NOT NULL UNIQUE,    CAPACITY            INT                           NOT NULL,    DATE_OF_MANUFACTURE DATE                          NOT NULL,    NUMBER_ROUTE        INT                           NOT NULL,    FOREIGN KEY (NUMBER_ROUTE) REFERENCES ROUTE (NUMBER_ROUTE) ON DELETE CASCADE);